select film.id_genre as 'id_genre', genre.`name` as 'name_genre', distrib.id_distrib as 'id_distrib', distrib.`name` as 'name_distrib', film.title as 'title_film' from film left join genre on film.id_genre = genre.id_genre left join distrib on film.id_distrib = distrib.id_distrib where film.id_genre >= 4 and film.id_genre <= 8;
